import {React, useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';

const Details = () => {
    const [productData, setProductData] = useState([]);
    let id = useParams().id;

    useEffect(() => {
        fetch(`http://localhost:5000/product/${id}`)
        .then(response => response.json())
        .then(data => {
            setProductData(data);
        })
        .catch(error => {
            console.error(error);
        })
    }, [id]);

    function create(event){
        event.preventDefault();
        console.log("Create is working fine");
        const title = productData.name;
        const url = productData.img;
        const money = productData.price;
        fetch(`http://localhost:5000/add-cart/`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({title, url, money})
        })
        .then(response => response.json())
        .then(data => {
            console.log(data);
        })
        .catch(error => {
            console.error(error);
        });
        window.location.href = "/cart";
    }

    if(Object.keys(productData).length > 0) {return (
        <form onSubmit={create}>
            <div className='container text-center justify-content-center' id="prodcontain">
                <div class="card mb-3" style={{width: "540px"}}>
                    <div class="row g-0">
                        <div class="col-md-4">
                            <img src={productData.img} class="img-fluid rounded-start" alt="..."/>
                        </div>
                        <div class="d-flex col-md-6" style={{height: "200px"}}>
                            <div class="vr"></div>
                        </div>
                        <div class="col-md-10">
                            <div class="card-body justify-content-center">
                                <h5 class="card-title">{productData.name}</h5>
                                <p class="card-text"><small class="text-body-secondary">{productData.category[0]} | {productData.category[1]}</small></p>
                                <p class="card-text">{productData.description}</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </form>
    )
}
}

export default Details
