import {React, useEffect, useState} from 'react';
import axios from "axios";
import Cookies from "universal-cookie";

const cookies = new Cookies();
const token = cookies.get("TOKEN");

const logout = () => {
    cookies.remove("TOKEN", {path: "/"});
    window.location.href = "/";
}

export default function Admin() {
    const [message, setMessage] = useState("");
    const [productData, setProductData] = useState([]);

    useEffect(() => {
        const config = {
            method: "get",
            url: "http://localhost:5000/admin-endpoint",
            headers: {
                Authorization: `Bearer ${token}`,
            },
        };
        axios(config)
        .then((result) => {
            setMessage(result.data.message);
        })
        .catch((e) => {
            e = new Error();
        });
    }, []);

    useEffect(() => {
        const config = {
            method: "get",
            url: "http://localhost:5000/admin-products",
            headers: {
                Authorization: `Bearer ${token}`,
            },
        };
        axios(config)
        .then((result) => {
            setProductData(result.data.products);
        })
        .catch((e) => {
            e = new Error();
        });
    }, []);
    
    return (
        <div>
            <div>Admin</div>
            <h2>{message}</h2>
            <div>
                {productData.map(data => (
                    <div key={data._id} id="proddisplay">
                        <div class="card" style={{width: "18rem"}}>
                            <img src={data.img} class="card-img-top " alt="productImg"/>
                            <div class="card-body">
                                <h4 class="card-title">{data.name}</h4>
                                <p class="card-text">${data.price}</p>
                                <a href="#" class="btn btn-primary" id="buttons">Go somewhere</a>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
            <button class="btn btn-danger" variant="danger" type="submit" onClick={() => logout()}>Logout</button>
        </div>
        )
}
