import {React, useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';

const Details = () => {
    const [productData, setProductData] = useState([]);
    let id = useParams().id;

    useEffect(() => {
        fetch(`http://localhost:5000/product/${id}`)
        .then(response => response.json())
        .then(data => {
            setProductData(data);
        })
        .catch(error => {
            console.error(error);
        })
    }, [id]);

    if(Object.keys(productData).length > 0) {return (
        <div>
            <div className="container text-center" id="prodcontain">
                <>
                <div class="card mb-3" style="max-width: 540px;">
                    <div class="row g-0">
                        <div class="col-md-4">
                            <img src={productData.img} class="img-fluid rounded-start" alt="productImg"/>
                        </div>
                        <div class="col-md-8">
                            <div class="card-body">
                                <h5 class="card-title">Card title</h5>
                                <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                                <p class="card-text"><small class="text-body-secondary">Last updated 3 mins ago</small></p>
                            </div>
                        </div>
                    </div>
                </div>
                    <div id="proddisplay">
                        <div className="card" style={{width: "18rem"}}>
                            <img src={productData.img} className="card-img-top " alt="productImg"/>
                            <div className="card-body">
                                <h4 className="card-title">{productData.name}</h4>
                                <p className="card-text">${productData.price}</p>
                            </div>
                        </div>
                    </div>
                </>
            </div>
        </div>
    )
}
}

export default Details
