import {React, useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';

const Details = () => {
    const [productData, setProductData] = useState([]);
    let id = useParams().id;

    useEffect(() => {
        fetch(`http://localhost:5000/product/${id}`)
        .then(response => response.json())
        .then(data => {
            setProductData(data);
        })
        .catch(error => {
            console.error(error);
        })
    });

    console.log(productData)

    if(productData) {return (
        <div>
            <div className="container text-center" id="prodcontain">
                {productData?.map(object => (
                <>
                    <div key={object._id} id="proddisplay">
                        <div className="card" style={{width: "18rem"}}>
                            <img src={object.img} className="card-img-top " alt="productImg"/>
                            <div className="card-body">
                                <h4 className="card-title">{object.name}</h4>
                                <p className="card-text">${object.price}</p>
                            </div>
                        </div>
                    </div>
                </>
                ))}
            </div>
        </div>
    )
}
}

export default Details
