const Cart = () => {
    const [productData, setProductData] = useState([]);

    useEffect(() => {
        fetch(`http://localhost:5000/cart`)
        .then(response => response.json())
        .then(data => {
            setProductData(data);
        })
        .catch(error => {
            console.error(error);
        })
    }, []);

    function go(id){
        console.log("Deleting product: ", id);
        if(id && id !== ""){
            fetch(`http://localhost:5000/deleteItem/${id}`, {
                method: "DELETE"
            })
            .then(response => response.json())
            .then(() => {
                fetch(`http://localhost:5000/cart`)
                .then(response => response.json())
                .then(data => {
                    setProductData(data);
                })
