import {React, useEffect, useState, useParams} from 'react';

const Details = () => {
    let id = useParams();
    const [productData, setProductData] = useState([]);

    useEffect(() => {
        fetch(`http://localhost:5000/product/${id}`)
        .then(response => response.json())
        .then(data => {
            setProductData(data);
        })
        .catch(error => {
            console.error(error);
        })
    }, []);

    return (
        <div>Details</div>
    )
}

export default Details
